package javaprojects;

public class operations 
{

   public static void main(String[] args) 
   {
		// TODO Auto-generated method stub
		int a=20;
		int b=30;
		
		int x= ++a + --b;
	System.out.println(x  + b + a);
	
//	1. AREA OF RECTANGLE 
		int l= 20;
		int w=15;
		int result0= l*w;
		System.out.println(result0);
		
//	2. CUBE OF NUMBER
	    int d=12;
	    int result= d*d*d;
	 
	 System.out.println(result);
	 
//	3. ADDITION, SUBTRACTION, MULTIPLICATION, DIVISION EXAMPLES
	    int g= 14;
	    int h= 15;
	    int result1= g+h;
	    int result2= g-h;
	    int result3= g*h;
	    int result4= g/h;
	    
//	    I. ADDITION
	    System.out.println(result1);
	    
//	    II. SUBTRACTION
	    System.out.println(result2);
	    
//	    III. MULTIPLICATION
	    System.out.println(result3);
	    
//	    IV. DIVISION
	    System.out.println(result4);
	    
	    
	    
	}

}
